'use strict';

const url = require('url');

module.exports = {
    isValidUrl: isValidUrl
};

function isValidUrl(urlString) {
    if (typeof urlString !== 'string' || !urlString.trim()) {
        return false;
    }
    try {
        new url.URL(urlString);
        return true;
    } catch (e) {
        return false;
    }
}
